    <div style={{
      background: 'var(--ink-2)', border: '1px solid var(--border)',
      borderRadius: '12px', padding: '20px 24px',
    }}>
      <div style={{ fontSize: '20px', marginBottom: '10px' }}>{icon}</div>
      <div style={{
        fontFamily: 'var(--font-display)', fontWeight: 700,
